interactiveExamples.code:
.gallery {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  grid-gap: 1rem;
  padding: 1rem;
}

.gallery-item {
  aspect-ratio: 1;
  background-color: #f0f0f0;
  display: flex;
  align-items: center;
  justify-content: center;
}

.dashboard {
  display: grid;
  grid-template-columns: 250px 1fr;
  grid-template-rows: auto 1fr auto;
  grid-template-areas:
    'sidebar header'
    'sidebar main'
    'sidebar footer';
  height: 100vh;
}

.dashboard-header { grid-area: header; background-color: #f5f5f5; padding: 1rem; }
.dashboard-sidebar { grid-area: sidebar; background-color: #e0e0e0; padding: 1rem; }
.dashboard-main { grid-area: main; padding: 1rem; }
.dashboard-footer { grid-area: footer; background-color: #f5f5f5; padding: 1rem; text-align: center; }

.card-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  grid-auto-rows: minmax(150px, auto);
  grid-gap: 1.5rem;
  padding: 2rem;
}

.card-grid-item {
  background-color: white;
  border-radius: 8px;
  padding: 1.5rem;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}

concepts.example:
@keyframes slide-in {
  from {
    transform: translateX(-100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.animated-element {
  animation: slide-in 0.5s ease-out;
}

.transition-element {
  transition: all 0.3s ease;
}

.transition-element:hover {
  transform: scale(1.1);
}

interactiveExamples.code:
@keyframes spin {
  to { transform: rotate(360deg); }
}

.loader {
  width: 40px;
  height: 40px;
  border: 4px solid #f3f3f3;
  border-top: 4px solid #3498db;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes fade-in {
  from { opacity: 0; }
  to { opacity: 1; }
}

.fade-element {
  animation: fade-in 1s ease-in forwards;
  opacity: 0; /* Initial opacity */
}

