  box-shadow: 0 5px 15px rgba(0,0,0,0.2);
}

concepts.example:
/* Base styles */
.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 1rem;
}

/* Responsive grid */
.grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1rem;
}

/* Media queries */
@media (max-width: 768px) {
  .grid {
    grid-template-columns: 1fr;
  }

  .header {
    font-size: 1.5rem;
  }
}

