concepts.example:
/* Specificity */
body {
  color: blue; /* Low specificity */
}

p {
  color: green; /* Higher specificity than body */
}

.text-class {
  color: orange !important; /* Highest specificity due to !important */
}

/* Inheritance */
.parent {
  font-family: Arial, sans-serif;
}

/* <p> will inherit font-family from .parent */

interactiveExamples.code:
