  color: purple;
}

.text-block {
  color: teal;
}

p#special-text.text-block {
  color: red; /* Try changing this and see the effect */
}

.parent-font {
  font-family: 'Courier New', monospace;
}

.child-text {
  color: orange;
  /* font-family is inherited from .parent-font */
}

/* Override inherited font-family */
.child-text-override {
  font-family: Arial, sans-serif !important;
}

/* Example with !important */
.important-style {
  color: blue !important;
}

.less-important-style {
  color: green;
}

/* Even though .less-important-style is defined later, .important-style takes precedence */

