// Example of different Doji patterns
const standardDoji = { time: '2023-01-01', open: 100, high: 105, low: 95, close: 100 };
const longLeggedDoji = { time: '2023-01-02', open: 100, high: 110, low: 90, close: 100 };
const dragonflyDoji = { time: '2023-01-03', open: 100, high: 101, low: 90, close: 100 };
const gravestoneDoji = { time: '2023-01-04', open: 100, high: 110, low: 99, close: 100 };

// Analyzing a Doji pattern
function analyzeDoji(candle) {
  const bodySize = Math.abs(candle.open - candle.close);
  const totalRange = candle.high - candle.low;

  if (bodySize / totalRange < 0.1) {
    return 'This is likely a Doji pattern, indicating market indecision';
  }
  return 'Not a Doji pattern';
}

