concepts.example:
// Example of a Hammer pattern
const hammerCandle = {
  time: '2023-01-01',
  open: 102,
  high: 103,
  low: 95,
  close: 102
};

// Identifying a Hammer pattern
function isHammer(candle) {
  const bodySize = Math.abs(candle.open - candle.close);
  const lowerWick = candle.open > candle.close ? candle.open - candle.low : candle.close - candle.low;
  const upperWick = candle.high - Math.max(candle.open, candle.close);

  if (lowerWick >= 2 * bodySize && upperWick <= bodySize / 2) {
    return 'This is likely a Hammer pattern';
  }
  return 'Not a Hammer pattern';
}

