// Example of a Bullish Engulfing pattern
const bullishEngulfingData = [
  { time: '2023-01-01', open: 105, high: 106, low: 100, close: 101 }, // Bearish candle
  { time: '2023-01-02', open: 101, high: 112, low: 100, close: 110 }  // Bullish engulfing candle
];

// Checking for Bullish Engulfing pattern
function isBullishEngulfing(candle1, candle2) {
  if (candle1.close < candle1.open && candle2.close > candle2.open) {
    if (candle2.open <= candle1.close && candle2.close >= candle1.open) {
      return 'This is a Bullish Engulfing pattern';
    }
  }
  return 'Not a Bullish Engulfing pattern';
}

