// Example of a Piercing Line pattern
const piercingLineData = [
  { time: '2023-01-01', open: 105, high: 106, low: 100, close: 101 }, // Bearish candle
  { time: '2023-01-02', open: 100, high: 110, low: 98, close: 108 }  // Bullish Piercing Line candle
];

// Checking for Piercing Line pattern
function isPiercingLine(candle1, candle2) {
  if (candle1.close < candle1.open && candle2.close > candle2.open) {
    const midpoint = candle1.open - (candle1.open - candle1.close) / 2;
    if (candle2.open < candle1.low && candle2.close > midpoint) {
      return 'This is a Piercing Line pattern';
    }
  }
  return 'Not a Piercing Line pattern';
}

