// Example of a Harami pattern
const haramiData = [
  { time: '2023-01-01', open: 115, high: 118, low: 110, close: 112 }, // Large bearish candle
  { time: '2023-01-02', open: 112, high: 113, low: 111, close: 112 }  // Small bullish candle within
];

// Checking for Harami pattern
function isHarami(candle1, candle2) {
  if (Math.abs(candle1.open - candle1.close) > Math.abs(candle2.open - candle2.close)) {
    if (candle2.open >= Math.min(candle1.open, candle1.close) && candle2.close <= Math.max(candle1.open, candle1.close) &&
        candle2.close >= Math.min(candle1.open, candle1.close) && candle2.open <= Math.max(candle1.open, candle1.close)) {
      return 'This is a Harami pattern';
    }
  }
  return 'Not a Harami pattern';
}

