concepts.example:
// Example of a Tweezer Tops pattern
const tweezerTopsData = [
  { time: '2023-01-01', open: 110, high: 115, low: 109, close: 114 }, // Bullish candle
  { time: '2023-01-02', open: 114, high: 115, low: 112, close: 113 }  // Bearish candle with matching high
];

// Checking for Tweezer Tops pattern (simplified - needs proximity check for highs)
function isTweezerTops(candle1, candle2) {
  const high1 = candle1.high;
  const high2 = candle2.high;
  const proximityThreshold = 0.005; // 0.5% proximity

  if (high2 >= high1 * (1 - proximityThreshold) && high2 <= high1 * (1 + proximityThreshold)) {
    return 'This is likely a Tweezer Tops pattern';
  }
  return 'Not a Tweezer Tops pattern';
}

