// Example of Three White Soldiers pattern
const threeWhiteSoldiersData = [
  { time: '2023-01-01', open: 100, high: 102, low: 98, close: 99 }, // Bearish trend candle
  { time: '2023-01-02', open: 99, high: 105, low: 98.5, close: 104 }, // 1st White Soldier
  { time: '2023-01-03', open: 104, high: 110, low: 103.5, close: 109 },// 2nd White Soldier
  { time: '2023-01-04', open: 109, high: 115, low: 108.5, close: 114 } // 3rd White Soldier
];

// (Simplified) Checking for Three White Soldiers pattern (needs body and close comparisons)
function isThreeWhiteSoldiers(candle1, candle2, candle3) {
  if (candle2.close > candle2.open && candle3.close > candle3.open) { // Ensure all are bullish
    if (candle2.open >= Math.min(candle1.open, candle1.close) && candle3.open >= Math.min(candle2.open, candle2.close)) { // Open within body
      if (candle2.close > candle1.close && candle3.close > candle2.close) { // Progressively higher closes
        return 'This is likely a Three White Soldiers pattern';
      }
    }
  }
  return 'Not a Three White Soldiers pattern';
}

