// Example of a Spinning Top pattern
const spinningTopCandle = {
  time: '2023-01-01',
  open: 110,
  high: 115,
  low: 105,
  close: 111
};

// Identifying a Spinning Top pattern
function isSpinningTop(candle) {
  const bodySize = Math.abs(candle.open - candle.close);
  const upperWick = candle.high - Math.max(candle.open, candle.close);
  const lowerWick = Math.min(candle.open, candle.close) - candle.low;
  const totalRange = candle.high - candle.low;

  if (bodySize / totalRange < 0.3 && upperWick / totalRange > 0.3 && lowerWick / totalRange > 0.3) { // Rough proportions
    return 'This is likely a Spinning Top pattern';
  }
  return 'Not a Spinning Top pattern';
}

