const fallingThreeMethodsData = [
  { time: '2023-01-01', open: 110, high: 112, low: 100, close: 102 }, // Long bearish candle
  { time: '2023-01-02', open: 102, high: 105, low: 101, close: 104 }, // Small bullish candle 1
  { time: '2023-01-03', open: 104, high: 106, low: 103, close: 105 }, // Small bullish candle 2
  { time: '2023-01-04', open: 105, high: 107, low: 104, close: 106 }, // Small bullish candle 3
  { time: '2023-01-05', open: 106, high: 108, low: 95, close: 97 }  // Long bearish candle confirming continuation
];

// (Simplified) Checking for Falling Three Methods pattern (needs range checks)
function isFallingThreeMethods(candle1, candle2, candle3, candle4, candle5) {
  if (candle1.close < candle1.open && candle5.close < candle5.open) { // 1st and 5th bearish
    if (candle2.close > candle2.open && candle3.close > candle3.open && candle4.close > candle4.open) { // Middle bullish
      if (candle2.high <= candle1.high && candle2.low >= candle1.low &&  // Contained within 1st
          candle3.high <= candle1.high && candle3.low >= candle1.low &&
          candle4.high <= candle1.high && candle4.low >= candle1.low &&
          candle5.close < candle1.close) { // 5th closes below 1st
        return 'This is likely a Falling Three Methods pattern';
      }
    }
  }
  return 'Not a Falling Three Methods pattern';
}

