];

// (Simplified) Checking for Morning Doji Star pattern (needs gap and Doji checks)
function isMorningDojiStar(candle1, candle2, candle3) {
  const isDoji = Math.abs(candle2.open - candle2.close) / (candle2.high - candle2.low) < 0.1; // Basic Doji check
  if (candle1.close < candle1.open && isDoji && candle3.close > candle3.open) {
    if (candle2.high < candle1.close && candle3.open > candle2.close) { // Gap conditions
      return 'This is likely a Morning Doji Star pattern';
    }
  }
  return 'Not a Morning Doji Star pattern';
}

