// Example of Stick Sandwich pattern
const stickSandwichData = [
  { time: '2023-01-01', open: 105, high: 106, low: 95, close: 96 }, // Long bearish candle 1
  { time: '2023-01-02', open: 95, high: 103, low: 94, close: 95.5 },// Bullish candle
  { time: '2023-01-03', open: 96, high: 97, low: 88, close: 95.8 } // Long bearish candle 2, similar close
];

// (Simplified) Checking for Stick Sandwich pattern (needs close proximity check)
function isStickSandwich(candle1, candle2, candle3) {
  const close1 = candle1.close;
  const close3 = candle3.close;
  const proximityThreshold = 0.005; // 0.5% proximity for closes

  if (candle1.close < candle1.open && candle2.close > candle2.open && candle3.close < candle3.open) {
    if (candle2.open < candle1.close && candle3.open > candle2.close) { // Open conditions
      if (close3 >= close1 * (1 - proximityThreshold) && close3 <= close1 * (1 + proximityThreshold)) { // Close proximity
        return 'This is likely a Stick Sandwich pattern';
      }
    }
  }
  return 'Not a Stick Sandwich pattern';
}

