    if (candle2.open < candle1.close && candle2.close > candle1.close && candle2.close < midpoint1) { // Gap down, close above neck line, below midpoint
      return 'This is likely a Thrusting Line pattern';
    }
  }
  return 'Not a Thrusting Line pattern';
}

