// (Simplified) Checking for Bullish Breakaway pattern (needs gap and price action checks)
function isBullishBreakaway(candle1, candle2, candle3, candle4, candle5) {
  if (candle1.close < candle1.open && candle2.close < candle2.open && candle3.close > candle3.open && candle4.close > candle4.open && candle5.close > candle5.open) {
    if (candle3.open < candle2.close && candle4.open > candle3.close) { // Gap conditions
      if (candle5.close > candle4.high) { // 5th candle confirms
        return 'This is likely a Bullish Breakaway pattern';
      }
    }
  }
  return 'Not a Bullish Breakaway pattern';
}

