// (Simplified) Checking for Bearish Tasuki Gap pattern (needs gap and gap fill check)
function isBearishTasukiGap(candle1, candle2) {
  if (candle1.close < candle1.open && candle2.close > candle2.open) {
    if (candle2.open < candle1.close && candle2.open > candle1.open) { // Gap down condition
      if (candle2.close < candle1.low) { // Bullish candle doesn't close gap
        return 'This is likely a Bearish Tasuki Gap pattern';
      }
    }
  }
  return 'Not a Bearish Tasuki Gap pattern';
}

